body.script:
function getParam(name) {
  const urlParams = new URLSearchParams(window.location.search);
  return urlParams.get(name);
}

const username = getParam("Username");
const statusParam = getParam("Status");

if (username && statusParam) {
  document.getElementById("usernameText").textContent = username;

  let status = statusParam.toLowerCase();
  let displayStatus = "";

  if (status === "online") {
    displayStatus = "Online";
  }
  else if (status === "idle") {
    displayStatus = "Idle";
  }
  else if (status === "offline" || status === "invisible") {
    displayStatus = "Invisible";
    status = "invisible";
  }
  else if (status === "dnd" || status === "do not disturb") {
    displayStatus = "Do Not Disturb";
    status = "dnd";
  }
  else if (status === "disturb") {
    displayStatus = "Do Not Disturb";
    status = "dnd";
  }

  document.getElementById("statusText").textContent = displayStatus;

  document.querySelectorAll(".status-dot").forEach(el => el.classList.remove("active"));

  switch (status) {
    case "online":
      document.getElementById("status-online").classList.add("active");
      break;
    case "idle":
      document.getElementById("status-idle").classList.add("active");
      document.getElementById("status-idle-moon").classList.add("active");
      break;
    case "invisible":
      document.getElementById("status-invisible").classList.add("active");
      document.getElementById("status-invisible-dot").classList.add("active");
      break;
    case "dnd":
      document.getElementById("status-dnd").classList.add("active");
      document.getElementById("status-dnd-bar").classList.add("active");
      break;
  }
}
